sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var total = 0;
    var max = 2500.00;

	//Create object of name-value pairs.
	var nvPairs = makePairs(control);

	//If at least 1 name has been entered
    if (Object.keys(nvPairs).length > 0) {

		//Iterate over all name-value pairs
        for (var key in nvPairs) {

			//If name actually has a value
            if (nvPairs[key]) {
                var value = parseFloat(nvPairs[key]);
                var sum = total + value;

				//Check if the maximum has been exceded.
				if (sum > max) {
                    alert('The total amount of expences exceeds the $2,500.00 maximum.\nThe reimbursement amount will be set to the maximum allowed\nrather than the actual sum of $' + sum);
                }
                total = sum > max ? max : sum;//Assign total.
            }
        }
		//Set the Reimbursement Amount field to the new total.
		g_form.setValue('u_amount', 'USD;'+ total.toString(),'$' + total.toString());
    }
	else{
		g_form.setValue('u_amount', 'USD;0.00','$0');
	}
}

function makePairs(control) {
	//Remove un-necessary characters, leaving a string of "name:value,name1:value1,..."
    var nvList = control.value.replace(/\$|"|{|}/g, '');

	//Split string at commas, creating array ["name:value","name1:value1",...].
    nvList = nvList.split(',');

    var obj = {};
    for (var i = 0; i < nvList.length; i++) {
		//Create individual pair.
        var nvPair = nvList[i].split(':');

		//Assign name as obj key = value.
        obj[nvPair[0].trim()] = nvPair[1].trim();
    }
    return obj;
}
