sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if(isLoading || newValue === ''){
		return;
	}

	var pairs = makePairs(control);

	var total = 0;
	var max = 2500.00;

	for(var key in pairs){
		if(pairs[key]){
			var value = parseFloat(pairs[key]);
			var sum = total + value;
			total = sum > max ? max : sum;
		}
	}

	g_form.setValue('u_amount', total.toString());

}

function makePairs(control){
	var expenceTypes = control.value.replace(/\$|"|{|}/g, '');
	expenceTypes = expenceTypes.split(',');

	var obj = {};
	for(var i = 0; i < expenceTypes.length; i++){
		var str = expenceTypes[i].split(':');
		obj[str[0].trim()] = str[1].trim();
	}
	return obj;
}
