sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }


	var newObj = JSON.parse(newValue);
	for(var key in newObj){
		var num = newObj[key];
		console.log(num);
	}
	var keys = Object.keys(newObj);
    var total = 0;
    var max = 2500.00;

    //Create object of name-value pairs.
    var nvPairs = makePairs(control);
    var keys = Object.keys(nvPairs);

    if (keys.length > 0) { //If at least 1 name has been entered
        if (doVerify(nvPairs, keys)) { // Check if value entered is valid
            for (var key in nvPairs) {
                if (nvPairs[key]) { // Check name actually has a value

                    var value = parseFloat(nvPairs[key]);
                    var sum = total + value;

                    if (sum > max) { //Alert if the maximum has been exceded.
                        alert('The total amount of expences exceeds the $2,500.00 maximum.\nThe reimbursement amount will be set to the maximum allowed\nrather than the actual sum of $' + sum);
                    }

                    total = sum > max ? max : sum; //Assign total.                					
                }
            }
            g_form.setValue('u_amount', 'USD;' + total.toString(), '$' + total.toString());
        }
    }
}

//Converts string to object of key value pairs.
function makePairs(control) {
    var nvList = control.value.replace(/\$|"|{|}/g, ''); //Remove un-necessary characters, leaving a string of "name:value,name1:value1,..."
    nvList = nvList.split(','); //Split string at commas, creating array ["name:value","name1:value1",...]

    var obj = {};
    for (var i = 0; i < nvList.length; i++) {
        var nvPair = nvList[i].split(':'); //Create individual pair.
        obj[nvPair[0].trim()] = nvPair[1].trim(); //Assign name as obj key = value.
    }
    return obj;
}

//Verifies that value entered is a number.
function doVerify(nvPairs, keys) {
    if (isNaN(nvPairs[keys[keys.length - 1]])) { //Check if most recent value entered is NaN.

        g_form.addErrorMessage('Invalid Value!\nValue must be number.');

        nvPairs[keys[keys.length - 1]] = ''; //Set the invalid value to empty string.
        var str = JSON.stringify(nvPairs); //Convert object to string.
        g_form.setValue('u_expense_type', str); //Reset value to the new string.

        g_form.flash('u_expense_type', '#ff0000', 0);

        return false; //valid = false

    } else {
        return true; //Valid = true
    }
}
