sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

 // get the travel type field
	var travelTypes = g_form.getValue("u_travel_type");
// 	clear the travel type options
	g_form.clearOptions("u_travel_type");

// 	If the new value of travel mode is flight
// 	travel type options are economy/bussiness class
	if(newValue == 'flight'){
		g_form.addOption('u_travel_type', 'economy', 'Economy');
		g_form.addOption('u_travel_type', 'bussiness', 'Bussiness Class');
	}

// 	If the new value of travel mode is train
// 	travel type options are class1/class2/class3
	if(newValue == 'train'){
		g_form.addOption('u_travel_type', 'class1', 'Class1');
		g_form.addOption('u_travel_type', 'class2', 'Class2');
		g_form.addOption('u_travel_type', 'class3', 'Class3');
	}

// 	If the new value of travel mode is Bus
// 	travel type options are Luxury Seater/ Semi sleeper/ full sleeper
	if(newValue == 'bus'){
		g_form.addOption('u_travel_type', 'luxury', 'Luxury Seater');
		g_form.addOption('u_travel_type', 'semi_sleeper', 'Semi Sleeper');
		g_form.addOption('u_travel_type', 'full_sleeper', 'Full Sleeper');
	}

}
