sys_script_client.script:
function onSubmit() {

// 	Check if this is a new record.
// 	If it is then get the total and display it.
    if (g_form.isNewRecord()) {
        var ga = new GlideAjax('ERF_Helper');
        ga.addParam('sysparm_name', 'getTotal');
        ga.getXML(parseResponse);
    }
// 	Otherwise do nothing.
	else{
		return;
	}
}

// Callback function to parse the XML response
function parseResponse(response) {
    var answer = response.responseXML.documentElement.getAttribute('answer');
    var prior = answer - 1;//Total number of records minus this one;
    alert('Your request will now be submited there are currently ' + prior + ' existing records, yours will be number: ' + answer);
}
