sys_script_include.script:
var ERF_Helper = Class.create();
ERF_Helper.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getEmployeeTotal: function() {

        var userID = gs.getUserID();
        var employee = this.getParameter('sysparm_employee');

        if (userID == employee) {
            var count = new GlideAggregate('x_393226_erf_employee_reimbursements');
            count.addEncodedQuery('u_employee=' + userID);
            count.addAggregate('COUNT');
            count.query();

            var total = 0;
            if (count.next()) {
                total = count.getAggregate('COUNT');
            }
            return total;

        } else {
            return false;
        }
    },

    // 	Returns the total number of records in the ERF table.
    getTotal: function() {

        var count = new GlideAggregate('x_393226_erf_employee_reimbursements');
        count.addAggregate('COUNT');
        count.query();

        var total = 0;
        if (count.next()) {
            total = count.getAggregate('COUNT');
        }

        return total;
    },

    type: 'ERF_Helper'
});
